import React, { useEffect, useState } from 'react';
import FullCalendar from '@fullcalendar/react'
import dayGridPlugin from '@fullcalendar/daygrid'
import interactionPlugin from "@fullcalendar/interaction"
import { useSelector } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { logoutUser } from './../actions/user.js';


const Calendar = (props) => {
    const user = useSelector(state => state.user);
    const [showNewTask, setshowNewTask] = useState(false);
    const [selectedDate, setselectedDate] = useState(null);

    const dispatch = useDispatch();
    let tasks = [];
    const handleColor = (statusText) => {
        if (statusText == 'accept') {
            return '#0e9b01';
        } else if (statusText == 'reject') {
            return '#fb414a';
        } else {
            return '#f7c52e';
        }
    }

    if (user.tasks && user.tasks.length > 0) {
        user.tasks.map(task => {
            tasks.push({
                'id': task.id,
                'title': task.title,
                'start': task.start,
                'color': handleColor(task.status_text)
            })
        })
    }


    const handleDateClick = (arg) => {
        setshowNewTask(true);
        let date = new Date();
        console.log(Date.now());
       // setselectedDate();
    }

    const handleEventClick = (arg) => {
    }

    const handleLogout = () => {
        dispatch(logoutUser());
        props.history.push("/");
    }
    return (

        user && user.email ? (
            <div className="container">
                <div className="row mt-5" >
                    <div className="col-6">
                        <h4>Welcome : {user.email} </h4>
                    </div>
                    <div className="col-6 text-right">
                        <button className="btn btn-sm btn-danger" onClick={handleLogout}> Logout</button>
                    </div>
                </div>
                {showNewTask ? (
                    <div className="row">
                        <div className="col-12 pt-5 pb-5">
                            <div className="card">
                                <div className="card-header">New Task for : </div>
                                <div className="card-body"></div>
                            </div>
                        </div>
                    </div>
                ) : null}
                <div className="row">
                    <div className="col-12 pt-5 pb-5">

                        <FullCalendar
                            plugins={[dayGridPlugin, interactionPlugin]}
                            initialView="dayGridMonth"
                            initialEvents={tasks} // alternatively, use the `events` setting to fetch from a feed
                            dateClick={handleDateClick}
                            eventClick={handleEventClick}
                        />

                    </div>
                </div>
            </div>
        ) : null


    );
}

export default withRouter(Calendar);
