import React, { useEffect } from 'react';
import FullCalendar from '@fullcalendar/react'
import dayGridPlugin from '@fullcalendar/daygrid'
import interactionPlugin from "@fullcalendar/interaction"
import { useSelector } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { logoutUser } from './../actions/user.js';


const Calendar = (props) => {
    const user = useSelector(state => state.user);
    const dispatch = useDispatch();
    let tasks = [];
    const handleColor = (statusText) => {
        if (statusText == 'accept') {
            return '#0e9b01';
        } else if (statusText == 'reject') {
            return '#fb414a';
        } else {
            return '#f7c52e';
        }
    }

    if (user.tasks && user.tasks.length > 0) {
        user.tasks.map(task => {
            tasks.push({
                'id': task.id,
                'title': task.title,
                'start': task.start,
                'color': handleColor(task.status_text)
            })
        })
    }


    const handleDateClick = (arg) => { 
    }

    const handleEventClick = (arg) => { 
    }

    const handleLogout = () => { 
        dispatch(logoutUser());
        props.history.push("/");
    }
    return (

        user && user.email ? (
            <div class="container">
                <div class="row mt-5" >
                    <div class="col-6">
                        <h4>Welcome : {user.email} </h4>
                    </div>
                    <div class="col-6 text-right">
                        <button class="btn btn-sm btn-danger" onClick={handleLogout}> Logout</button>
                    </div>
                </div>
                <div class="row">
                    <div class="col-12 pt-5 pb-5">
                        <FullCalendar
                            plugins={[dayGridPlugin, interactionPlugin]}
                            initialView="dayGridMonth"
                            initialEvents={tasks} // alternatively, use the `events` setting to fetch from a feed
                            dateClick={handleDateClick}
                            eventClick={handleEventClick}
                        />
                    </div>
                </div>
            </div>
        ) : null


    );
}

export default withRouter(Calendar);
