export const setUser = user => {
    return async dispatch => {
      await dispatch({type: 'SET_USER', payload: user});
    };
};
export const logoutUser = () => {
    return async dispatch => {
      await dispatch({type: 'LOGOUT_USER', payload: null});
    };
};
